class Pairing implements Comparable<Pairing> {
	long idx;
	long value;

	public Pairing(long idx, long value) {
		this.idx = idx;
		this.value = value;
	}

	@Override
	public int compareTo(Pairing pair) {
		if (pair.idx < idx) {
			return 1;
		} else if (pair.idx > idx) {
			return -1;
		}
		return 0;
	}

}
